const dropParent=document.querySelector(".drop-down-blocks");dropParent.addEventListener("click",e=>{var e=e.target.closest(".post"),t=e.querySelector(".question"),s=e.querySelector(".answer");e.classList.contains("open")?(e.style.maxHeight=0,e.classList.remove("open")):(e.classList.add("open"),e.style.maxHeight=e.offsetHeight+s.offsetHeight+t.offsetHeight-26+"px")});